import { useState } from 'react';
import './ExpenseForm.css';
const ExpenseForm = (props) => {
    const [inputTitle, setInputTitle] = useState('');
    const [inputAmount, setInputAmount] = useState('');
    const [inputDate, setInputDate] = useState('');
    // const [userInput, setUserInput] = useState({
    //     inputTitle: '',
    //     inputAmount: '',
    //     inputDate: ''
    // });
    const titleChangeHandler = (event) => {
        setInputTitle(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     inputTitle: event.target.value});
        // setUserInput((prevState) => { 
        //     return{...prevState, inputTitle: event.target.value}
        // });

    };
    const amountChangeHandler = (event) => {
        setInputAmount(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     inputAmount: event.target.value});
        // setUserInput((prevState) => { 
        //     return{...prevState, inputAmount: event.target.value}
        // });

    };
    const dateChangeHandler = (event) => {
        setInputDate(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     inputDate: event.target.value});
        // setUserInput((prevState) => { 
        //     return{...prevState, inputDate: event.target.value}
        // });
    };

    const clearState = () => {
        setInputTitle('');
        setInputAmount('');
        setInputDate('');
    }
    const submitHandler = (event) => {
        event.preventDefault();
        const expenseData = {
            title: inputTitle,
            amount: inputAmount,
            date: new Date(inputDate)
        };
        // console.log(expenseData);
        props.onSaveExpenseData(expenseData);
        clearState();
    };

    return (
        <form onSubmit={submitHandler}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" value={inputTitle} onChange={titleChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min="0.01" step="0.01" value={inputAmount} onChange={amountChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min="2019-01-01" max="2022-12-31" value={inputDate} onChange={dateChangeHandler}/>
                </div>
            </div>
            <div className="new-expense__actions">
                <button>Cancel</button>
                <button type="submit">Add Expense</button>
            </div>
        </form>
    );
};
export default ExpenseForm;
